pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// @author Joseph Anderson
// @title This is Pegged Palladium token
contract PeggedPalladium is ERC20, Ownable {
  mapping(address => bool) private whitelists;

  event WhitelistSet(address indexed to, bool value);

  constructor() ERC20("Pegged Palladium", "PPALD") {}

  /*  
    @return Return decimals of this token
    @dev This token's decimal is 0
    Invalid Calculations (Decimals): We want to clarify that the reason for the 0 decimals is that these tokens cannot be factionalized. 
    Each token must be wholly redeemable for its underlying metal. 
    (Please add in any additional risks of having a token with 0 decimal places)
  */
  function decimals() public view virtual override returns (uint8) {
    return 0;
  }

  /* 
      @param address: This is wallet address to set whitelist
      @param value: If value is true, set this address to whitelist
      @dev set address to be whiltelisted or disable it
      Highly Permissive Role Access (Mint and Whitelist): We want to clarify that Symbridge controls the supply of the token because it is backed 1 – 1 by real world assets. 
      Since each token must be physically backed, Symbridge will not mint tokens unless there is a physical metal to back the token.
  */
  function setWhiteList(address to, bool value) external onlyOwner {
    whitelists[to] = value;

    emit WhitelistSet(to, value);
  }

  // @param amount: Mint token to owner address by amount
  // @dev Mint token to sender address by amount
  function mint(uint256 amount) external onlyOwner {
    _mint(msg.sender, amount);
  }

  // @param amount: Burn token by amount from sender
  // @dev Burn token from sender by amount
  function burn(uint256 amount) external virtual onlyOwner {
    require(balanceOf(msg.sender) >= amount, "PPALD: Insufficient token balance");
    _burn(_msgSender(), amount);
  }

  // @return If address is whitelisted, it returns true. Else it returns false
  // @param address: This is address of wallet to get whitelist setting
  // @dev This function set whitelist setting by address
  function isWhitelist(address to) public view returns (bool) {
    return whitelists[to];
  }

  /* @dev Undocumented behavior (Whitelist): We want the solution for this to be clarifying this feature publicly. 
          Make it known that this feature is part of how the token is set up and that Symbridge treats this as a centralized aspect. 
          Symbridge wants to be able to verify the entities whose wallet will be trading pegged Palladium. (It is not meant for decentralized trading)
  */
  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 amount
  ) internal virtual override {
    require(
      isWhitelist(to) || to == owner() || to == address(0),
      "PPALD: Receiver is not whitelist"
    );
  }
}
